css:
.print-format *{
    color:black !important;
}

.print-format table{
    font-size: 12px !important;
}

.print-format table td{
    padding: 4px !important;
}

.print-format td.py-0{
    padding-top: 0px !important;
    padding-bottom: 0px !important;
}

.print-format td.vertical-middle{
    vertical-align: middle !important;
}

.print-format td.vertical-bottom{
    vertical-align: bottom !important;
}

.print-format td.vertical-top{
    vertical-align: top !important;
}

.fs-10{
    font-size: 10px !important;
}

.bg-orange{
    background-color: #D49B04 !important;
}

.bg-orange-light{
    background-color: #FDFBEC !important;
}

.bg-gray{
    background-color: #808080 !important;
}

.bg-light-gray{
    background-color: #F8F8F8;
}

.red{
    color: #800000 !important;
}

.blue{
    color: #000080 !important;
}

.black{
    color: #000 !important;
}

.white{
    color: #fff !important;
}

.bold{
    font-weight: bold;
}

.italic{
    font-style: italic;
}

.text-center{
    text-align: center;
}

.text-right{
    text-align: right;
}

.w-100{
    width:100%;
}

.w-90{
    width:90%;
}

.w-60{
    width:60%;
}

.w-50{
    width:50%;
}

.w-40{
    width:40%;
}

.w-25{
    width:25%;
}

.w-33{
    width:33%;
}

.w-20{
    width:20%;
}

.w-15{
    width:15%;
}

.mx-16{
    margin-top:16px;
    margin-bottom:16px;
}
.my-0{
    margin-top:0px;
    margin-bottom: 0px;
}

.mb-16{
    margin-bottom:16px;
}

.mb-50{
    margin-bottom:50px;
}

.mr-16{
    margin-right:16px;
}

.mt-16{
    margin-top:16px;
}


.py-4{
    padding-top:4px;
    padding-bottom:4px;
}

.pt-25{
    padding-top:25px;
}

.gray-border-bottom{
    border-bottom: 2px solid lightgray;
}


.gray-border-top{
    border-top: 2px solid lightgray;
}

.black-border-bottom{
    border-bottom: 2px solid black;
}

.box-shadow{
    box-shadow: 4px 4px 0px black;
}

.h-line{
   width:100%;
   height: 2px;
   background-color:lightgray;
}

.no-break {
    page-break-inside: avoid;
}

@media print {
    body{
        padding-top:14px !important;
        padding-bottom:0px !important;
    }
}
